import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from "@fullcalendar/interaction"; 
import { 
    Container, 
    Box, 
    Button, 
    Modal, 
    ModalContent, 
    ModalHeader, 
    ModalFooter, 
    Select, 
    ModalBody, 
    Input, 
    FormControl, 
    FormLabel,
    } from '@chakra-ui/react';
import { useEffect, useState } from 'react'
import Nav from './Nav';
import Cookies from 'js-cookie'

import useAuth from './UseAuth';




const Schedule = () => {

    useAuth();

    //isOpen state variable for the "addShift" Modal
    const [ isOpen, setIsOpen ] = useState(false)
    const [ employees, setEmployees ] = useState([])
    const [ shifts, setShifts ] = useState([])

    //state variables for the "editEvent" modal
    const [ eventModalOpen, setEventModalOpen ] = useState(false)

    // state variables to hold values of addShift form
    const [ student, setStudent ] = useState('') 
    const [date, setDate ] = useState('')
    const [ startTime, setStartTime ] = useState('')
    const [ endTime, setEndTime ] = useState('')

    // state variable to hold selected event after event click
    const [ selectedEvent, setSelectedEvent ] = useState([])


    const authToken = Cookies.get("authToken")


    //retrive all employees
    useEffect(() => {
        fetch('/employee/get_all_employees/', {
            method: 'GET',
            headers: {
                'Authorization': `Token ${authToken}`,
            },
        })
            .then(response => response.json())
            .then(json => {
                console.log(json, 'fetched all employees')
                const data = json.map(item => ({
                    name : `${item.first_name} ${item.last_name}`,
                    id : item.id
                }));
                setEmployees(data)
            }).catch(error => {
                console.error('Error:', error);
            })
    },[] )


    //retrieve all shifts
    useEffect(() => {
        fetch('/shift/list_shifts/', {
            method: 'GET',
            headers: {
                'Authorization': `Token ${authToken}`,
            },
        })
            .then(response => response.json())
            .then(json => {
                console.log(json, 'fetched all shifts')
                const data = json.map(item => ({
                    id: item.id,
                    title : item.student,
                    start : `${item.date}T${item.start_time}`,
                    end : `${item.date}T${item.end_time}`
                }));
                setShifts(data)
            }).catch(error => {
                console.error('Error:', error);
            })
    }, [])



    const resetInputs = () => {
        setStudent('')
        setDate('')
        setEndTime('')
        setStartTime('')
    }

    const data = {

        "start_time": startTime,
        "end_time": endTime,
        "date": date,
        "student": student
    }

    //add shift onClose
    const onClose = () => {
        if (!student || !date || !startTime || !endTime) {
            alert('All fields must be filled in before submitting.');
            return;
        }


        fetch('/shift/add_shift/', {
            method : 'POST',
            headers : {
                'Content-type' : 'application/json',
                'Authorization': `Token ${authToken}`
            },
            body : JSON.stringify(data)
        })
            .then(response => response.json())
            .then(json => {
                console.log(json, 'added a shift')
                fetch('/shift/list_shifts/', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Token ${authToken}`,
                    }
                })
                    .then(response => response.json())
                    .then(json => {
                        const data = json.map(item => ({
                            title : item.student,
                            start : `${item.date}T${item.start_time}`,
                            end : `${item.date}T${item.end_time}`
                        }));
                        console.log(json, 'fetched all shifts')
                        setShifts(data)
                    }).catch(error => {
                        console.error('Error:', error);
                    })
            })
            .catch(error => console.log(error))


        resetInputs()
        setIsOpen(false)
    }

    //update Shift
    const updateShift = () => {

        setEventModalOpen(false)

    }

    const deleteShift = () => {
        setEventModalOpen(false)

    }



    return (
        <>
            <Nav/>

            <Box height="1rem" />

            <Container maxH='20vh' maxW='container.xl' >

                {/* add shift modal */}
                <Modal isOpen={isOpen} onClose={onClose} isCentered={true}>
                    <ModalContent>
                        <ModalHeader>Enter Shift Details</ModalHeader>
                        <ModalBody>
                            <FormControl>
                                <FormLabel>Student</FormLabel>
                                    <Select placeholder='Select student' onChange={(e) => setStudent(e.target.value)}>
                                        {employees.map((item) => {
                                            return (
                                                <option key={item.id} value={item.id}>{item.name}</option>
                                            )
                                        })}
                                    </Select>
                            </FormControl>
                            <FormControl>
                                <FormLabel>Date</FormLabel>
                                    <Input type='date' onChange={(e) => setDate(e.target.value)} value={date}/>
                            </FormControl>
                            <FormControl>
                                <FormLabel>Start Time</FormLabel>
                                    <Input type='time' value={startTime} onChange={(e) => setStartTime(e.target.value)} />
                            </FormControl>
                            <FormControl>
                                <FormLabel>End Time</FormLabel>
                                    <Input type='time' value={endTime} onChange={(e) => setEndTime(e.target.value)} />
                            </FormControl>

                        </ModalBody>
                        <ModalFooter>
                            <Button 
                                onClick={() => {
                                    setIsOpen(false);
                                    resetInputs();
                                }} 
                                color='red'>Cancel</Button>

                            <Button onClick={onClose}>Submit</Button>
                        </ModalFooter>
                    </ModalContent>
                </Modal>

                {/* edit event modal */}
                <Modal isOpen={eventModalOpen} onClose={updateShift} isCentered={true}>
                    <ModalContent>
                        <ModalHeader>Edit Shift</ModalHeader>
                        <ModalBody>
                            <FormControl>
                                <FormLabel>Student</FormLabel>
                                    <Select placeholder='Select student' onChange={(e) => setStudent(e.target.value)}>
                                        {employees.map((item) => {
                                            return (
                                                <option key={item.id} value={item.id}>{item.name}</option>
                                            )
                                        })}
                                    </Select>
                            </FormControl>
                            <FormControl>
                                <FormLabel>Date</FormLabel>
                                    <Input type='date' onChange={(e) => setDate(e.target.value)} value={date}/>
                            </FormControl>
                            <FormControl>
                                <FormLabel>Start Time</FormLabel>
                                    <Input type='time' value={startTime} onChange={(e) => setStartTime(e.target.value)} />
                            </FormControl>
                            <FormControl>
                                <FormLabel>End Time</FormLabel>
                                    <Input type='time' value={endTime} onChange={(e) => setEndTime(e.target.value)} />
                            </FormControl>

                        </ModalBody>
                        <ModalFooter display='flex' justifyContent='space-between'>

                            <Button onClick={deleteShift} color='red'>Delete</Button>

                            <Box>
                                <Button 
                                    onClick={() => {
                                        setEventModalOpen(false);
                                        resetInputs();
                                    }} 
                                    color='red'>Cancel</Button>
                                <Button onClick={updateShift}>Submit</Button>
                            </Box>
                        </ModalFooter>
                    </ModalContent>
                </Modal>

                <Button onClick={() => setIsOpen(true)}>Add Shift</Button>
                <FullCalendar
                    plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                    initialView="timeGridWeek"
                    weekends={false}
                    events={shifts}
                    slotMinTime="07:00:00"
                    slotMaxTime="22:00:00"
                    eventColor="#378006"
                    selectable={true}
                    slotEventOverlap={false}
                    allDaySlot={false}
                    eventClick={(info) => {
                        // setDate(info.event.startStr.substring(0,10))
                        // setStartTime(info.event.startStr.substring(11,16)) 
                        // setEndTime(info.event.endStr.substring(11,16)) 
                        // setSelectedEvent(info.event)
                        // setEventModalOpen(true);
                        alert('you clicked this buddy')
                    }}      
                />
            </Container>
        </>

    );
}

export default Schedule
