import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from "@fullcalendar/interaction"; 

import {
    Box,
    Button,
    Container,
    Flex,
    Heading,
    Stack,
    Text,
    useColorModeValue,
} from '@chakra-ui/react';

import Nav from './Nav';
import * as Icons from 'react-icons/fc';

import useAuth from './UseAuth';

import { useContext, useCallback, useEffect } from 'react';
import EmployeeShiftContext from './EmployeeShiftContext';

import useRole from './useRole';
import Cookies from 'js-cookie';

const Card = (props) => {
  const Icon = Icons[props.icon];
  return (
    <Box
      maxW={{ base: 'full', md: '275px' }}
      w={'full'}
      borderWidth="1px"
      borderRadius="lg"
      overflow="hidden"
      p={5}>
      <Stack align={'start'} spacing={2}>
        <Flex
          w={16}
          h={16}
          align={'center'}
          justify={'center'}
          color={'white'}
          rounded={'full'}
          bg={useColorModeValue('gray.100', 'gray.700')}>
          <Icon size="32" />
        </Flex>
        <Box mt={2}>
          <Heading size="md">{props.heading}</Heading>
          <Text mt={1} fontSize={'sm'}>
            {props.description}
          </Text>
        </Box>
        <Button variant={'link'} colorScheme={'blue'} size={'sm'}>
          Learn more
        </Button>
      </Stack>
    </Box>
  );
};

const Dashboard = () => {


  useAuth();
  const authToken = Cookies.get("authToken")
  const role = useRole()
  const { shifts, adjustments, setAdjustments } = useContext(EmployeeShiftContext)


  const fetchAdjustments = useCallback(() => {

    fetch('retrieve_adjustments', {
      method: 'GET',
      headers: {
        'Authorization': `Token ${authToken}`,
      },
    })
      .then(response => response.json())
      .then(json => {
        const data = json.map(adjustment => ({
          employee : json.employee,
          date : json.date,
          shift_id : json.shift_id,
          type_of_coverage : json.type_of_coverage,
          start : json.start,
          end : json.end,
        }))

        setAdjustments(data)
      })




  }, [authToken, setAdjustments])

  useEffect(() => fetchAdjustments, [fetchAdjustments])

  return (
      <>
        <Nav />
        <Box p={4}>
        <Stack spacing={4} as={Container} maxW={'3xl'} textAlign={'center'}>
            <Heading fontSize={{ base: '2xl', sm: '4xl' }} fontWeight={'bold'}>

                { !role && <Text>Welcome to the Employee Dashboard</Text>}
                { role && <Text>Welcome to the Supervisor Dashboard</Text>}
            </Heading>
        </Stack>

        <Container maxW={'5xl'} mt={12}>
            <Flex flexWrap="wrap" gridGap={6} justify="center">
              <Card
                  heading={'Total Hours'}
                  icon={'FcAssistant'}
                  description={
                  'Here are the number of hours you are scheduled for this week: '
                  }
              />
              <Card
                  heading={'Shifts available for coverage: '}
                  icon={'FcDonate'}
                  description={
                    'Lorem ipsum dolor sit amet catetur, adipisicing elit.'
                  }
              />
              <Card
                  heading={'Time Off Requests'}
                  icon={'FcManager'}
                  description={
                    'Lorem ipsum dolor sit amet catetur, adipisicing elit.'
                  }
              />
              <Container>
                <Box>
                  <Heading mb={4}>Time off requests</Heading>
                  {adjustments.map((adjustment) => {
                    return (
                      <Text key={adjustment.shift_id}>{adjustment.employee} is requesting {adjustment.type_of_coverage} on {adjustment.date}</Text>
                    )
                  })}


                </Box>
              </Container>

            </Flex>

            <Box >
              <Stack spacing={2}>
                <Box mt={2}>
                  <Heading size="md">Schedule for today</Heading>
                  <FullCalendar
                    plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                    initialView="timeGridDay"
                    weekends={false}
                    events={shifts}
                    slotMinTime="07:00:00"
                    slotMaxTime="22:00:00"
                    eventColor="#378006"
                    selectable={false}
                    slotEventOverlap={false}
                    allDaySlot={false}      
                />
                </Box>
              </Stack>
            </Box>
        </Container>
        </Box>
        </>
    );
  }

export default Dashboard;
