import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from "@fullcalendar/interaction"; 

import { Container, Box, Button, Modal, ModalContent, ModalHeader, ModalFooter, Select, ModalBody, Input } from '@chakra-ui/react';

import { useEffect, useState } from 'react'
import Nav from './Nav';




const Schedule = () => {

    const [ isOpen, setIsOpen ] = useState(false)

    const [ employees, setEmployees ] = useState([])


    // state variables to hold values of 
    const [ student, setStudent ] = useState('') 





    useEffect(() => {
        fetch('/employee/get_all_employees/')
            .then(response => response.json())
            .then(json => {
                const data = json.map(item => ({
                    name : `${item.first_name} ${item.last_name}`,
                    id : item.id
                }));
                setEmployees(data)
            }).catch(error => {
                console.error('Error:', error);
            })
    }, [])


    const shifts = [

        { title: 'John', start: '2023-05-011T09:00:00', end: '2023-05-11T14:00:00', backgroundColor : 'red' },
        { title: 'Jim', start: '2023-05-11T12:00:00', end: '2023-05-11T17:00:00' },
        { title: 'Snoopy', start: '2023-05-11T16:00:00', end: '2023-05-11T20:00:00' },
        { title: 'Snoopy', start: '2023-05-11T09:00:00', end: '2023-05-11T22:00:00' },
        { title: 'Jane', start: '2023-05-12T10:00:00', end: '2023-05-12T15:00:00' },

      ];

      const handleAdd = () => {

        setIsOpen(true)


      }

      const onClose = () => {
        setIsOpen(false)

      }



    return (
        <>
            <Nav/>

            <Box height="1rem" />

            <Container maxH='20vh' maxW='container.xl' >

                <Modal isOpen={isOpen} onClose={onClose} isCentered={true}>
                    <ModalContent>
                        <ModalHeader>Enter Shift Details</ModalHeader>
                        <ModalBody>
                            <Select placeholder='Select student' onChange={(e) => setStudent(e.target.value)}>
                                {employees.map((item) => {
                                    return (
                                        <option key={item.id} value={item.name}>{item.name}</option>
                                    )
                                })}
                            </Select>
                            <Input value></Input>
                        </ModalBody>
                        <ModalFooter>
                            <Button onClick={onClose}>Close</Button>
                        </ModalFooter>
                    </ModalContent>
                </Modal>

                <Button onClick={handleAdd}>Add Shift</Button>
                <FullCalendar
                    plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                    initialView="timeGridWeek"
                    weekends={false}
                    events={shifts}
                    slotMinTime="07:00:00"
                    slotMaxTime="22:00:00"
                    eventColor="#378006"
                    selectable={true}
                    slotEventOverlap={false}
                    allDaySlot={false}
                    eventClick={() => alert('lol you clicked this fool')}      
                />
            </Container>
        </>

    );
}

export default Schedule
